        public List<CasaRural> getCasasRurales(){
            return new ArrayList<>(casasRurales.values());
        }

        public CasaRural getCasaRuralById(String id){
            return casasRurales.get(id);
        }

        public Reserva getReservaByCod(String cod){
        return reservas.get(cod);
    }

        public List<Usuario> getUsuariosRegistrados(){
            return new ArrayList<>(usuariosRegistrados.values());
        }

        public void addUsuariosRegistrados(String email, String contrasenna){
            usuariosRegistrados.put(email, new Usuario(email, contrasenna));
        }

        public Usuario getUsuarioByEmail(String email){
        return usuariosRegistrados.get(email);
    }

        public void addReservas(Reserva reserva){
            reservas.put(reserva.getCodReserva(), reserva);
        }

        public void deleteReserva(Reserva reserva){
            reservas.remove(reserva.getCodReserva(),reserva);
        }

        public List<Reserva> getReservas(){
            return new ArrayList<>(reservas.values());
        }

        public SimpleDateFormat getFormatoFecha(){
            return formatoFecha;
        }

        public Date convertirDate (String fechaString){
            Date fecha;

            try {
                fecha = formatoFecha.parse(fechaString);
                return fecha;
            } catch (ParseException e) {
                return null;
            }
        }

